import React, { useState } from 'react';
import { createParticipant } from '../services/api';

export default function ParticipantForm() {
  const [formData, setFormData] = useState({
    fullName: '',
    birthDate: '',
    role: '',
    email: '',
    phone: ''
  });

  const [status, setStatus] = useState('');
  const [xml, setXml] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus('⏳ Отправка...');
    try {
      const res = await createParticipant(formData);
      setStatus('✅ Участник успешно создан!');
      setXml(res.data.xmlPreview);
    } catch (err) {
      const errorText = err.response?.data?.error || 'Ошибка отправки';
      setStatus('❌ ' + errorText);
      setXml('');
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: '0 auto' }}>
      <h2>Создание участника</h2>
      <form onSubmit={handleSubmit}>
        {['fullName', 'birthDate', 'role', 'email', 'phone'].map((field) => (
          <div key={field} style={{ marginBottom: '1rem' }}>
            <label>{field}:</label>
            <input
              type={field === 'birthDate' ? 'date' : 'text'}
              name={field}
              value={formData[field]}
              onChange={handleChange}
              required
              style={{ width: '100%', padding: 8 }}
            />
          </div>
        ))}
        <button type="submit">Создать</button>
      </form>
      <div style={{ marginTop: 16 }}>{status}</div>
      {xml && (
        <pre style={{
          marginTop: 16, padding: 10,
          background: '#f4f4f4', border: '1px solid #ccc',
          whiteSpace: 'pre-wrap'
        }}>{xml}</pre>
      )}
    </div>
  );
}
